import { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      monsters : [
        {
          name : 'Charmendar',
          id : '123e'
        },
        {
          name : 'Charmelion',
          id : '345f'
        },
        {
          name : 'Charizard',
          id : '789g'
        },
        {
          name : 'Bulbasaur',
          id : '134r'
        }
      ]
    }
  }

  render(){
    return (
      <div className="App">
      {this.state.monsters.map((monster)=>{
        return (
          <div key={monster.id}>
            <h1>{monster.name}</h1>
          </div> 
        )
      })}
    </div>
    )
  }
}

export default App;
