import { Component } from "react";

class CardList extends Component{

    

    render(){

        const {monsters} = this.props

        return (
            <div className="card-list">
               {monsters.map((pokemon,index) => (
                    <div className="card-container" key={index + 1}>
                        <img className="image" src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${index+1}.svg`} alt={pokemon.name} />
                        <h2>{pokemon.name}</h2>
                        <p>{pokemon._id}</p>
                    </div>
               ))}

            </div>
        )
    }
}

export default CardList;
