import { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import CardList from './components/card-list/card-list.component';
import SearchBox from './components/search-box/search-box.component'

class App extends Component {

  constructor(){
    super();

    this.state = {
      monsters : [],
      searchField : ''
    }
  }

componentDidMount(){
  fetch('https://pokeapi.co/api/v2/pokemon')
  .then((response) => response.json())
  .then((pokemons)=>{
    this.setState(() =>{
      return {monsters:pokemons.results};
    })
  })
}

OnSearchChange = (event) => {
  const searchField = event.target.value.toLocaleLowerCase();
  this.setState(() => {
    return { searchField }
  })
}



  render(){

    const {monsters,searchField} = this.state;
    const {OnSearchChange} = this

    let filteredMonsters = monsters.filter((pokemon) => {
      return (pokemon.name.toLocaleLowerCase().includes(searchField));
    });

    return (
      <div className="App">

      <SearchBox className = 'Search-box' onChangeHandler = {OnSearchChange} placeholder = "Who's that pokemon" />
      <CardList  monsters = {filteredMonsters} />
    </div>
    )
  }
}

export default App;
