import { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      monsters : []
    }
  }

componentDidMount(){
  fetch('https://pokeapi.co/api/v2/pokemon')
  .then((response) => response.json())
  .then((pokemons)=>{
    this.setState(() =>{
      return {monsters:pokemons.results};
    },
    () =>{
      console.log(this.state)
    })
  })
}





  render(){
    return (
      <div className="App">
        <input 
        className = "search-box" 
        type = "search" 
        placeholder='Who is that pokemon' 
        onChange={(events) => {
          let updated = this.state.monsters.filter((pokemon) => {
            return (pokemon.name.includes(events.target.value));
          })
          if(events.target.value > 0){
            this.setState(() => {
              return {
                monsters : updated
              }
            },() => {
              console.log(this.state)
            })
          }else{
            this.setState(() => {
              return {
                monsters : this.state.monsters
              }
            })
          }

        }}
      />

      {this.state.monsters.map((monster,index)=>{
        return (
          <div key={index}>
            <h1>{monster.name}</h1>
          </div> 
        )
      })}
    </div>
    )
  }
}

export default App;
