import { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      monsters : []
    }
  }

componentDidMount(){
  fetch('https://pokeapi.co/api/v2/pokemon')
  .then((response) => response.json())
  .then((pokemons)=>{
    this.setState(() =>{
      return {monsters:pokemons};
    },
    () =>{
      console.log(this.state)
    })
  })
}





  render(){
    return (
      <div className="App">
      {this.state.monsters.map((monster)=>{
        return (
          <div key={monster.id}>
            <h1>{monster.name}</h1>
          </div> 
        )
      })}
    </div>
    )
  }
}

export default App;
